    const [count, setCount] = useReducer(countReducer, 0)

    function countReducer(prevState, nextState) {
        switch (action.type) {
            case 'increment': 
            return state + action.payload
            break;
            case 'decrement': 
            return state - action.payload
            break;
        }
    }



    return (  
        <div className={css.container}>
            <button onClick={() => setCount({type: 'increment', payload: 1})} className={css.btn} type="button">+</button>
            <p className={css.value}>{count}</p>
            <button onClick={() => setCount({type: 'decrement', payload: 1})} className={css.btn} type="button">-</button>
        </div>
    );
}

export default Counter ;
