import css from './Counter.module.css'

function countReducer(state, action) {
    switch (action.type) {
        case 'increment':
            return {...state, count: state.count + action.payload}
            break;
        case 'decrement':
            return {...state, count: state.count - action.payload}
            break;
        default: return state;
